	def sort_treeview(self, treeview, coluna):
			dados = [(int(treeview.set(item, coluna)), item) for item in treeview.get_children("")]
			dados.sort()
			for index, (valor, item) in enumerate(dados):
				treeview.move(item, "", index)
	
	def clear_widgets(self, widget):
		for child in widget.winfo_children():
			self.clear_widgets(child) 
			if isinstance(child, tk.Entry):
				child.delete(0, tk.END)
			if isinstance(child, ttk.Combobox):
				child.set("")
			if isinstance(child, tk.Listbox):
				child.selection_clear(0, tk.END)
			if isinstance(child, ttk.Treeview):
				for item in child.get_children():
					child.delete(item)
			if isinstance(child, tk.Text):
				child.delete("1.0", "end")
			if isinstance(child, (tk.Checkbutton, ttk.Checkbutton)):
				if child == self.woman_check_button:
					self.woman_var.set(0)
				if child == self.man_check_button:
					self.man_var.set(0)
				for var in self.document_vars.values():
					var.set(0)
			if isinstance(child, tk.Spinbox):
				child.delete(0, tk.END)
				child.insert(0, "00")
	

	

