	def show_home_frame(self):
		self.home_frame.tkraise()
		# roda o carregamento dos dados em background
		threading.Thread(target=self.load_data_in_background, daemon=True).start()

	def load_data_in_background(self):
		try:
			# Busca os pagamentos
			response = crud_support.select_all_payments()

			if not response or len(response) == 0:
				self.top.after(0, self.home_frame.tkraise)
				return

			# Filtra e ordena dados dos pagamentos
			def parse_data(item):
				try:
					return datetime.strptime(item["data_limite"], "%Y-%m-%d")
				except:
					return datetime.max
			limite_dias = 15
			hoje = datetime.today().date()

			response_filtrada = [
				item for item in response
				if item.get("data_limite") and not item.get("data_conclusao")
			]

			response_filtrada = [
				item for item in response_filtrada
				if datetime.strptime(item["data_limite"], "%Y-%m-%d").date() <= hoje + timedelta(days=limite_dias)
			]
			response_ordenada = sorted(response_filtrada, key=parse_data)

			# Busca dados de processos
			pre_process = crud_support.select_clients_by_process_step(1)
			waiting_contract = crud_support.select_clients_by_process_step(2)
			pending_documents = crud_support.select_clients_by_process_step(3)
			completed_documents = crud_support.select_clients_by_process_step(4)
			ongoing = crud_support.select_clients_by_process_step(6)
			seguimento = crud_support.select_clients_by_process_step(5)

			# Busca eventos do calendário
			calendar_events = crud_support.select_all_calendar_events()

			# Atualiza a GUI
			self.top.after(
				0,
				self.update_gui,
				response_ordenada, hoje,
				pre_process, waiting_contract, pending_documents,
				completed_documents, seguimento, ongoing,
				calendar_events
			)

		except Exception as e:
			self.top.after(0, lambda e=e: messagebox.showerror("Erro", f"Erro ao buscar dados: {e}"))


	def update_gui(self, pagamentos, hoje,
				   pre_process, waiting_contract, pending_documents,
				   completed_documents, seguimento, ongoing,
				   calendar_events):

		self.late_payments_frame.tkraise()

		# ===================================================
		# TREEVIEW DE PAGAMENTOS
		# ===================================================
		colunas = ["cliente_id", "nome_completo", "data_limite", "montante"]
		nomes_colunas = {
			"cliente_id": "Cliente ID",
			"nome_completo": "Nome",
			"data_limite": "Data Limite",
			"montante": "Montante"
		}

		self.list_of_late_payments_treeview.delete(*self.list_of_late_payments_treeview.get_children())
		self.list_of_late_payments_treeview["columns"] = colunas
		self.list_of_late_payments_treeview["show"] = "headings"

		for col in colunas:
			if col == "nome_completo":
				largura = 120
			elif col in ["cliente_id"]:
				largura = 60
			else:
				largura = 100
			self.list_of_late_payments_treeview.heading(col, text=nomes_colunas[col])
			self.list_of_late_payments_treeview.column(col, width=largura, anchor="center")

		self.list_of_late_payments_treeview.tag_configure('vencido', background='red')
		self.list_of_late_payments_treeview.tag_configure('proximo', background='yellow')
		self.list_of_late_payments_treeview.tag_configure('normal', background='white')

		for item in pagamentos:
			item["nome_completo"] = item.get("cliente", {}).get("nome_completo", "")
			data_limite_str = item.get("data_limite")

			try:
				data_limite = datetime.strptime(data_limite_str, "%Y-%m-%d").date()
			except:
				data_limite = None

			valores = [item.get(col, "") for col in colunas]

			if data_limite:
				if data_limite < hoje:
					tag = 'vencido'
				elif data_limite <= hoje + timedelta(days=10):
					tag = 'proximo'
				else:
					tag = 'normal'
			else:
				tag = 'normal'

			self.list_of_late_payments_treeview.insert("", "end", values=valores, tags=(tag,))


		# ===================================================
		# TREEVIEW PENDING DOCUMENTS
		# ===================================================
		for item in waiting_contract: item["_source"] = "waiting"
		for item in pending_documents: item["_source"] = "pending"
		for item in completed_documents: item["_source"] = "completed"
		for item in pre_process: item["_source"] = "pre_process"
		for item in seguimento: item["_source"] = "seguimento"
		for item in ongoing: item["_source"] = "ongoing"

		all_pending_docs = waiting_contract + pending_documents + completed_documents + pre_process + seguimento

		tree = self.pending_documents_treeview
		tree.delete(*tree.get_children())

		colunas_docs = ["cliente_id", "nome_completo", "entidade", "fase", "data_fase", "observação"]
		nomes_colunas_docs = {
			"cliente_id": "Cliente ID",
			"nome_completo": "Nome",
			"entidade": "Entidade",
			"fase": "Fase",
			"data_fase": "Data da Fase",
			"observação": "Observação"
		}

		tree["columns"] = colunas_docs
		tree["show"] = "headings"

		for col in colunas_docs:
			largura = 120 if col == "nome_completo" else 60 if col in ["cliente_id"] else 100
			tree.heading(col, text=nomes_colunas_docs[col])
			tree.column(col, width=largura, anchor="center")

		tree.tag_configure("waiting", background="white")
		tree.tag_configure("pending", background="yellow")
		tree.tag_configure("completed", background="light green")
		tree.tag_configure("pre_process", background="light blue")
		tree.tag_configure("seguimento", background="orange")

		for item in all_pending_docs:
			valores = [item.get(col, "") for col in colunas_docs]
			tag = item.get("_source", "waiting")
			tree.insert("", "end", values=valores, tags=(tag,))


		# ===================================================
		# TREEVIEW ONGOING
		# ===================================================
		tree = self.ongoing_process_treeview
		tree.delete(*tree.get_children())

		colunas_docs_1 = ["cliente_id", "nome_completo", "entidade", "data_fase", "observação"]
		nomes_colunas_docs_1 = {
			"cliente_id": "Cliente ID",
			"nome_completo": "Nome",
			"entidade": "Entidade",
			"data_fase": "Data da Fase",
			"observação": "Observação"
		}

		tree["columns"] = colunas_docs_1
		tree["show"] = "headings"

		for col in colunas_docs_1:
			largura = 120 if col == "nome_completo" else 60 if col in ["cliente_id"] else 100
			tree.heading(col, text=nomes_colunas_docs_1[col])
			tree.column(col, width=largura, anchor="center")

		for item in ongoing:
			valores = [item.get(col, "") for col in colunas_docs_1]
			tree.insert("", "end", values=valores)


		# ===================================================
		# TREEVIEW CALENDAR
		# ===================================================
		tree = self.calendar_home_treeview
		tree.delete(*tree.get_children())

		colunas_calendar = ["cliente_id", "nome_completo", "data_evento", "hora_evento", "motivo"]
		nomes_colunas_calendar = {
			"cliente_id": "Cliente ID",
			"nome_completo": "Nome",
			"data_evento": "Data",
			"hora_evento": "Hora",
			"motivo": "Motivo"
		}

		tree["columns"] = colunas_calendar
		tree["show"] = "headings"

		for col in colunas_calendar:
			largura = 120 if col == "nome_completo" else 80 if col in ["data_evento", "hora_evento"] else 60
			tree.heading(col, text=nomes_colunas_calendar[col])
			tree.column(col, width=largura, anchor="center")

		# Filtro de eventos: de hoje até 7 dias
		hoje = datetime.now().date()
		limite = hoje + timedelta(days=7)

		for item in calendar_events:
			item["nome_completo"] = item.get("cliente", {}).get("nome_completo", "")

			dt_raw = item.get("data_inicio")
			if isinstance(dt_raw, str):
				try:
					dt = datetime.fromisoformat(dt_raw.replace("Z", ""))
				except ValueError:
					continue
			elif isinstance(dt_raw, datetime):
				dt = dt_raw
			else:
				continue

			data_evento_date = dt.date()

			# Ignora eventos passados ou além de 7 dias
			if not (hoje <= data_evento_date <= limite):
				continue

			data_evento = dt.strftime("%Y-%m-%d")
			hora_evento = dt.strftime("%H:%M")
			motivo = item.get("motivo", "")

			valores = [
				item.get("cliente_id", ""),
				item.get("nome_completo", ""),
				data_evento,
				hora_evento,
				motivo
			]

			# Tags opcionais de cor
			if data_evento_date == hoje:
				tag = "hoje"
			elif data_evento_date <= hoje + timedelta(days=3):
				tag = "proximo"
			else:
				tag = "normal"

			tree.insert("", "end", values=valores, tags=(tag,))

		# Configuração de cores para os eventos
		tree.tag_configure("hoje", background="lightgreen")
		tree.tag_configure("proximo", background="yellow")
		tree.tag_configure("normal", background="white")


