	def converter_dados_cliente(self, data):
		def safe_split(data_str, index):
			try:
				return data_str.split("-")[index]
			except:
				return ""

		def get_valor(campo):
			return data.get(campo) if data.get(campo) else ""

		def get_mes_texto(data_str):
			try:
				return crud_support.month_to_string(int(data_str.split("-")[1]))
			except:
				return ""

		return {
			"cliente_id": get_valor("cliente_id"),
			"nome_completo": get_valor("nome_completo"),
			"nif": get_valor("nif"),
			"niss": get_valor("niss"),
			"bi_cc_titulo_residência": get_valor("bi_cc_titulo_residência"),
			"passaporte": get_valor("passaporte"),
			"email": get_valor("email"),
			"profissão": get_valor("profissão"),
			"nacionalidade": get_valor("nacionalidade"),
			"naturalidade": get_valor("naturalidade"),
			"estado_civil": get_valor("estado_civil"),
			"local_emissao_passaporte": get_valor("local_emissão_passaporte"),
			# validade BI/CC
			"validade_bi_dia": safe_split(data.get("validade_bi_cc", ""), 2),
			"validade_bi_mes": get_mes_texto(data.get("validade_bi_cc", "")),
			"validade_bi_ano": safe_split(data.get("validade_bi_cc", ""), 0),
			# emissão BI/CC
			"emissao_bi_dia": safe_split(data.get("emissão_bi_cc", ""), 2),
			"emissao_bi_mes": get_mes_texto(data.get("emissão_bi_cc", "")),
			"emissao_bi_ano": safe_split(data.get("emissão_bi_cc", ""), 0),
			# data de nascimento
			"nascimento_dia": safe_split(data.get("data_nascimento", ""), 2),
			"nascimento_mes": get_mes_texto(data.get("data_nascimento", "")),
			"nascimento_ano": safe_split(data.get("data_nascimento", ""), 0),
			# validade passaporte
			"validade_passaporte_dia": safe_split(data.get("validade_passaporte", ""), 2),
			"validade_passaporte_mes": get_mes_texto(data.get("validade_passaporte", "")),
			"validade_passaporte_ano": safe_split(data.get("validade_passaporte", ""), 0),
			# emissão passaporte
			"emissao_passaporte_dia": safe_split(data.get("emissão_passaporte", ""), 2),
			"emissao_passaporte_mes": get_mes_texto(data.get("emissão_passaporte", "")),
			"emissao_passaporte_ano": safe_split(data.get("emissão_passaporte", ""), 0),
			# gênero (retorna bools para os checkbuttons)
			"genero_masculino": data.get("gênero") == "Masculino",
			"genero_feminino": data.get("gênero") == "Feminino",
			# endereço
			"rua": get_valor("rua"),
			"numero_rua": get_valor("numero_rua"),
			"complemento": get_valor("complemento"),
			"localidade": get_valor("localidade"),
			"codigo_postal_1": safe_split(data.get("código_postal", ""), 0),
			"codigo_postal_2": safe_split(data.get("código_postal", ""), 1),
			# telefone
			"ddi": get_valor("ddi"),
			"contato": get_valor("contato"),
			"notas_documento": get_valor("notas_documento")
		}

