	def detectar_mudanças(self, antigo, novo):
		mudanças = {}
		for campo, novo_valor in novo.items():
			valor_original = antigo.get(campo)
			if novo_valor and novo_valor != valor_original:
				mudanças[campo] = novo_valor
		return mudanças

	def save_data_button_function(self):
		try:
			if not self.client_id_fix.get().strip():
				# Novo cliente + novo processo
				client_input = self.get_client_input()
				self.novo_cliente(client_input)

				process_input = self.get_process_input()
				self.novo_processo(process_input)

				check_list = self.get_checkbuttons_state()
				crud_support.save_client_documents(self.client_id_fix.get(), check_list)

			else:
				# Cliente já existente
				old_client_input = self.converter_dados_cliente(
					crud_support.select_client_by_id(self.client_id_fix.get())
				)
				new_client_input = self.get_client_input()
				mudanças_cliente = self.detectar_mudanças(old_client_input, new_client_input)
				print("Mudanças no cliente:", mudanças_cliente)
				self.editar_cliente(mudanças_cliente)
				check_list = self.get_checkbuttons_state()
				crud_support.save_client_documents(self.client_id_fix.get(), check_list)
				# Tentar obter process_id
				process_id = self.process_id_fix.get().strip()

				# Se não tiver process_id, tentar buscar no banco
				if not process_id:
					processos_existentes = crud_support.search_processes_by_client_id(self.client_id_fix.get())
					if processos_existentes and isinstance(processos_existentes, list) and len(processos_existentes) > 0:
						process_id = processos_existentes[0].get("processo_id")
						self.normal_state_ids()
						self.process_id_fix.delete(0, 'end')
						self.process_id_fix.insert(0, process_id)
						self.readonly_state_ids()
					
				# Agora com process_id garantido (ou não), decidir o que fazer
				if process_id and self.create_new_process_checker == 0:
					old_process_input = self.converter_dados_processo(
						crud_support.select_process_by_process_id(process_id)
					)
					new_process_input = self.get_process_input()
					mudanças_processo = self.detectar_mudanças(old_process_input, new_process_input)
					self.editar_processo(mudanças_processo)
					
				else:
					# Ainda não tem processo? Cria novo
					process_input = self.get_process_input()
					self.novo_processo(process_input)
					self.create_new_process_checker = 0

			messagebox.showinfo("Sucesso", "Dados salvos com sucesso.")

		except ValueError as ve:
			messagebox.showerror("Erro de validação", str(ve))
		except Exception as e:
			messagebox.showerror("Erro inesperado", str(e))


			
