	def gravar_edição_etapa(self):
		if not hasattr(self, 'etapa_id_atual') or not self.etapa_id_atual:
			messagebox.showwarning("Atenção", "Nenhuma etapa foi selecionada para edição.")
			return

		try:
			observacao_nova = self.note_step_box.get("1.0", "end").strip()
			nome_fase_nova = self.step_list_combobox.get()
			fase_id_nova = self.fases_dict.get(nome_fase_nova)

			# Buscar etapa original do banco
			etapa_atual = crud_support.select_process_step_by_step_id(self.etapa_id_atual)
			fase_id_atual = etapa_atual.get("fase_id")
			observacao_atual = (etapa_atual.get("observação") or "").strip()

			# Comparar com dados atuais
			dados = {}
			if fase_id_nova != fase_id_atual:
				dados["fase_id"] = fase_id_nova
			if observacao_nova != observacao_atual:
				dados["observação"] = observacao_nova

			if not dados:
				messagebox.showinfo("Nada a salvar", "Nenhuma modificação foi feita.")
				self.step_list_combobox.set('')
				self.note_step_box.delete("1.0", "end")
				return

			crud_support.update_step(self.etapa_id_atual, dados)
			messagebox.showinfo("Sucesso", "Etapa atualizada com sucesso.")
			self.show_process_steps(self.process_id_fix.get()) 
			self.step_list_combobox.set('')
			self.note_step_box.delete("1.0", "end")

		except Exception as e:
			print("Erro ao atualizar etapa:", e)
			messagebox.showerror("Erro", f"Erro ao atualizar etapa: {e}")


