	def converter_dados_financeiros(self, data):
		def safe_split(data_str, index):
			try:
				return data_str.split("-")[index]
			except:
				return ""

		def get_valor(campo):
			return data.get(campo) if data.get(campo) else ""

		def get_mes_texto(data_str):
			try:
				return crud_support.month_to_string(int(data_str.split("-")[1]))
			except:
				return ""

		return {
			"pagamento_id": get_valor("pagamento_id"),
			"entidade": get_valor("entidade"),
			"referencia": get_valor("referencia"),
			"montante": get_valor("montante"),
			# data limite
			"data_limite_dia": safe_split(data.get("data_limite", ""), 2),
			"data_limite_mes": get_mes_texto(data.get("data_limite", "")),
			"data_limite_ano": safe_split(data.get("data_limite", ""), 0),
			# data conclusão
			"data_conclusao_dia": safe_split(data.get("data_conclusao", ""), 2),
			"data_conclusao_mes": get_mes_texto(data.get("data_conclusao", "")),
			"data_conclusao_ano": safe_split(data.get("data_conclusao", ""), 0),
			# motivo e status
			"motivo": data.get("motivo", {}).get("motivo", "") if data.get("motivo") else "",
			"status": data.get("status_pagamento", {}).get("status", "") if data.get("status_pagamento") else ""
		}

	def show_payment_list(self):
		self.normal_state_ids()
		self.clear_widgets(self.finance_tab_notebook)
		self.readonly_state_ids()
		try:
			response = crud_support.select_list_of_payments(self.client_id_fix.get())

			if response and len(response) > 0:
				# Mapeamento: chave no dict -> nome que aparece na Treeview
				colunas_desejadas = {
					"pagamento_id": "ID",
					"montante": "Montante",
					"data_limite": "Data limite",
					"data_conclusao": "Data de conclusão",
					"status": "Status",  # vamos criar essa coluna a partir de status_pagamento
					"motivo": "Motivo"
				}

				# Força todas as colunas do mapeamento, mesmo que não estejam no dict original
				colunas = list(colunas_desejadas.keys())

				self.list_of_payments_treeview.delete(*self.list_of_payments_treeview.get_children())
				self.list_of_payments_treeview["columns"] = colunas
				self.list_of_payments_treeview["show"] = "headings"

				# Configura cabeçalhos
				for col in colunas:
					self.list_of_payments_treeview.heading(col, text=colunas_desejadas[col])
					if col == "pagamento_id":
						self.list_of_payments_treeview.column(col, width=56, anchor="center")
					else:
						self.list_of_payments_treeview.column(col, width=120, anchor="center")

				# Configura cores das tags
				self.list_of_payments_treeview.tag_configure("vencido", background="#ff9999")
				self.list_of_payments_treeview.tag_configure("alerta", background="#fff099")
				self.list_of_payments_treeview.tag_configure("pago", background="#b3ffb3")
				self.list_of_payments_treeview.tag_configure("default", background="#ffffff")

				hoje = datetime.now().date()

				for item in response:
					valores = []
					for col in colunas:
						if col == "status":
							status_pagamento = item.get("status_pagamento")
							if isinstance(status_pagamento, dict):
								valor = status_pagamento.get("status", "")
							else:
								valor = ""
						elif col == "motivo":
							motivo = item.get("motivo")
							if isinstance(motivo, dict):
								valor = motivo.get("motivo", "")
							else:
								valor = ""
						else:
							valor = item.get(col, "")
						valores.append(valor)

					# Determina cor da linha
					tag = "default"
					try:
						data_limite = item.get("data_limite") if item.get("data_limite") else None
						if isinstance(data_limite, str):
							data_limite = datetime.strptime(data_limite, "%Y-%m-%d").date()

						data_conclusao = item.get("data_conclusao") if item.get("data_conclusao") else None
						if data_conclusao:
							tag = "pago"
						elif data_limite:
							if data_limite < hoje:
								tag = "vencido"
							elif hoje <= data_limite <= hoje + timedelta(days=10):
								tag = "alerta"
							elif data_limite > hoje + timedelta(days=20):
								tag = "default"
					except:
						pass

					self.list_of_payments_treeview.insert("", "end", values=valores, tags=(tag,))

		except Exception as e:
			print(f"Erro: {e}")

	def get_payment_input(self):
		try:
			inputs = {
				"entidade": self.entidade_box.get(),
				"referencia": self.referencia_box.get(),
				"montante": self.payment_value_box.get(),
				"data_limite_dia": self.valid_day_payment_box.get(),
				"data_limite_mes": self.valid_month_payment_combobox.get(),
				"data_limite_ano": self.valid_year_payment_box.get(),
				"data_conclusao_dia": self.conclusion_day_payment_box.get(),
				"data_conclusao_mes": self.conclusion_month_payment_combobox.get(),
				"data_conclusao_ano": self.conclusion_year_payment_box.get(),
				"motivo_nome": self.reason_payment_combobox.get(),
				"status_nome": self.state_payment_combobox.get(),
				"cliente_id": self.client_id_fix.get()
			}

			# Limpar strings vazias e converter tipos
			def limpa(v):
				v = v.strip() if isinstance(v, str) else v
				return None if v == "" else v

			inputs = {k: limpa(v) for k, v in inputs.items()}

			# Converte manualmente os campos numéricos
			for campo in ["entidade", "referencia", "data_limite_dia", "data_limite_ano", "data_conclusao_dia", "data_conclusao_ano"]:
				if inputs[campo] is not None:
					inputs[campo] = int(inputs[campo])

			return inputs
		except Exception as e:
			raise ValueError(f"Erro ao validar dados do pagamento: {e}")

		
