	def document_generator(self):
		tipo_documento = self.type_of_document_combobox.get().strip()

		if not tipo_documento:
			messagebox.showerror("Erro", "Escolha um tipo de documento para gerar.")
			return

		data = self.get_data_to_contract()
		hoje = datetime.now()
		data["data_documento"] = f"{hoje.day} de {crud_support.month_to_string(hoje.month).lower()} de {hoje.year}"

		if tipo_documento in ["Contrato de Prestaçao de Serviços Jurídicos- Ação Intimação",
						"Contrato de Prestação de Serviços Jurídicos - Nacionalidade"]:
			try:
				valor = self.value_of_contract_box.get().strip()
				parcela = self.value_by_parcel_box_for_contract.get().strip()
				#sinal = self.initial_apport_box_for_contract.get().strip()
				mes = self.start_month_combobox_for_contract.get().strip()
				n_parcelas = self.number_parcels_box_for_contract.get().strip()
				if not valor or not parcela or not n_parcelas:
					messagebox.showerror("Erro", "Todos os valores devem ser preenchidos.")

				valor_int = int(valor)
				parcela_int = int(parcela)
				#sinal_int = int(sinal)

				data["valor_contrato_string"] = num2words(valor_int, lang='pt')
				data["valor_contrato_div_parcelas_string"] = num2words(parcela_int, lang='pt')
				#não é necessario para um dos clientes
				#data["sinal_string"] = num2words(sinal_int, lang='pt')

			except Exception as e:
				messagebox.showerror(
								"Erro ao converter valores",
								"Certifique-se de preencher os valores apenas com números inteiros.\n"
								"Exemplos válidos: 1000, 250, 75\n"
								"Evite formatos como: 1000.00, 1.000, 500,00"
							)
				return

		try:
			gerador_docs.gerar_documento(tipo_documento, data)
			messagebox.showinfo("Sucesso", "Documento gerado com sucesso!")
			self.clear_widgets(self.automatic_contract_frame)
		except Exception as e:
			messagebox.showerror("Erro", f"Erro ao gerar documento:\n{e}")

